import React, { useState } from "react";
import WelcomePage from "./WelcomePage";
import LibrarySystem from "./LibrarySystem";

const App = () => {
  const [view, setView] = useState("welcome");

  return (
    <div>
      {view === "welcome" ? (
        <div>
          <WelcomePage />
          <button onClick={() => setView("library")}>go to Library</button>
          </div>
          ) : (
            <LibrarySystem />
          )}
    </div>
  );
};

export default App;
